query:
// show everything
MATCH rel=()-->()
RETURN rel

//set RDF ressource types AS LPG labels/types
MATCH (s)<-[:SUBJECT]-(t)-[:OBJECT]->(o),
(t)-[:PREDICATE]->(p WHERE p.uri = "http://www.w3.org/1999/02/22-rdf-syntax-ns#type")
SET s:$(split(o.uri,"#")[1]);

// PatternDefinedLabels are Label
MATCH (l:PatternDefinedLabel) SET l:Label;

// PatternDefinedRelationships are Relationships
MATCH (r:PatternDefinedRelationship) SET r:Relationship;

// Symmetric are Relationships
MATCH (r:Symmetric) SET r:Relationship;

// set name, pattern, classElementVariable, propertyOwnerVariable, valueVariable, sourceElementVariable, targetElementVariable
UNWIND ["name", "pattern", "classElementVariable", "propertyOwnerVariable", "valueVariable", "sourceElementVariable", "targetElementVariable"] AS prop_name
CALL (prop_name) {
  MATCH (s)<-[:SUBJECT]-(t)-[:OBJECT]->(o),
  (t)-[:PREDICATE]->(p WHERE p.uri = "http://neo4j.com/lpg-ontology#"+prop_name)
  SET s[prop_name] = o.uri
};

// Merge HAS_PROPERTY, SCO, SOURCE, TARGET, IMPLIES, EQUIVALENT relationships
UNWIND ["HAS_PROPERTY", "SCO", "SOURCE", "TARGET", "IMPLIES", "EQUIVALENT"] AS rel_type
CALL (rel_type) {
  MATCH (s)<-[:SUBJECT]-(t)-[:OBJECT]->(o),
  (t)-[:PREDICATE]->(p WHERE p.uri = "http://neo4j.com/lpg-ontology#"+rel_type)
  MERGE (s)-[r:$(rel_type)]->(o)
};

// show LPG ontology
MATCH rel=(:!Triple)-->(:!Triple)
RETURN rel
